import { Data, animate, Override, Animatable } from "framer"

const data = Data({ scale: Animatable(1) })

Data.sliderButton1 = () => {
    console.log("1")
}

Data.sliderButton2 = () => {
    console.log("2")
}

Data.sliderButton3 = () => {
    console.log("3")
}

export const Scale: Override = () => {
    return {
        scale: data.scale,
        onTap() {
            data.scale.set(0.6)
            animate.spring(data.scale, 1)
        },
    }
}
